const email = global.get('email2pdf');


const options = {
                  format: 'A4',
                  footer: {
                      height: "20px"
                  },
                  header: {
                      height: "20px"
                  },
                  renderDelay: 100
              };

const pdf = email.htmlPdf;

var send = function(err,res) {
    if (err) {
            node.error(err,msg);
        } else {
            msg.payload = res;
            node.send(msg);
        }
};

pdf.create(msg.payload, options).toBuffer(function(err, buffer){
    send(err,buffer);
});

return msg;
